import React from 'react';
import { Feather} from '@expo/vector-icons';
import {useNavigation} from '@react-navigation/native';
import * as MailComposer from 'expo-mail-composer';
import {View, Image, Text, TouchableOpacity, Linking } from'react-native';

import logoImg from '../../assets/logo.png';
import styles from './styles';

export default function Datail(){
  const navigation= useNavigation();
  const massage= 'Olá APAD, estou entrando em contato pois gostaria de ajudar no caso "Cadelinha atropelada" com o valor de R$ 120,00.'

  function navigationBack(){
    navigation.goBack()
  }

  function sendMail(){
    MailComposer.composeAsync({
      subject:'Heroi do caso: Cadelinha atropelada',
      recipients: ['andremagaldi@gmail.com'],
      body: massage,
    })


  }

  function sendWhatsapp(){
    Linking.openURL(`whatsapp://send?phone=5531992840065&text=${massage}`)

  }

  return(
    <View style={styles.container}>

      <View style={styles.header}>
           
           <Image  source={logoImg}/>
            

            <TouchableOpacity onPress={navigationBack}>
            <Feather name='arrow-left' size={28} color="#e02041" />
            </TouchableOpacity>
        </View>
        <View style={styles.incident}>
            <Text style={[styles.incidentProperty,{marginTop:0}]}>Caso</Text>
            <Text style={styles.incidentValue}>Cadelinha atropelada.</Text>
            
            <Text style={styles.incidentProperty}>Descrição</Text>
            <Text style={styles.incidentValue}>
              A cadelinha julie foi atropelada no  bairro Santana e teve
              que passar por uma cirurgia as pressas.
            </Text>
            
            <Text style={styles.incidentProperty}>Valor</Text>
            <Text style={styles.incidentValue}>R$ 120,00</Text>
            
            <View>
            <Text style={styles.incidentProperty}>Ong</Text>
            </View>
          </View>

          <View style={styles.contactBox}>
          
            <Text style={styles.heroTitle}>Salve o dia!</Text>
            <Text style={styles.heroTitle}>seja o Heroi desse caso.</Text>
            
            <Text style={styles.heroDescription}>Entre em contato:</Text> 

          </View>

          <View style={styles.actions}>
            
            <TouchableOpacity style={styles.action} onPress={sendWhatsapp}>
            
              <Text style={styles.actionText}>WhatsApp</Text> 

            </TouchableOpacity>
              
                
            
            <TouchableOpacity style={styles.action} onPress={sendMail}>

              <Text style={styles.actionText}>E-mail</Text>

            </TouchableOpacity>
            
           </View>
        
    </View>
  );
}
